mapper.select:
SELECT ds.dutchauction_register_no, ds.dutchauction_successfulbid_price, ds.dutchauction_successfulbid_date, ds.dutchauction_checkin_date, ds.dutchauction_chechout_date, ds.auction_state_category_name FROM dutchauction_register AS d INNER JOIN dutchauction_successfulbid AS ds ON d.dutchauction_no = ds.dutchauction_register_no
WHERE d.member_id = #{memberId}
WHERE ds.member_id = #{memberId}
ORDER BY dutchauction_successfulbid_date DESC
