mapper.select:
  	SELECT COUNT(*)
  	FROM reverseauction_register


	  	  ${sk} LIKE #{sv}

	  	  AND reverseauction_rooming_date > #{date1}

	  	  AND reverseauction_leaving_date < #{date2}

	  	  AND reverseauction_rooming_date > #{date1} AND reverseauction_leaving_date < #{date2}

  	SELECT *
  	FROM reverseauction_register


	  	  ${sk} LIKE #{sv}


	  	  AND reverseauction_rooming_date > #{date1}

	  	  AND reverseauction_leaving_date < #{date2}

	  	  AND reverseauction_rooming_date > #{date1} AND reverseauction_leaving_date < #{date2}

	ORDER BY reverseauction_register_date DESC
	LIMIT #{pageMaker.startRow},#{pageMaker.rowPerPage}
